CREATE TRIGGER trg_create_participante_on_insert_exposicion AFTER INSERT ON participante_exposicion FOR EACH ROW WHEN NEW.estado IN ('seleccionado', 'confirmado') AND (NEW.participante_id IS NULL OR NEW.participante_id = 0) BEGIN
INSERT OR IGNORE INTO evento_edicion_participante (evento_edicion_id, artista_id, estado) VALUES (NEW.evento_edicion_id, NEW.artista_id, 'activo');
UPDATE participante_exposicion SET participante_id = (SELECT id FROM evento_edicion_participante WHERE artista_id = NEW.artista_id AND evento_edicion_id = NEW.evento_edicion_id LIMIT 1) WHERE id = NEW.id;
END;
CREATE TRIGGER trg_create_participante_on_update_exposicion AFTER UPDATE ON participante_exposicion FOR EACH ROW WHEN NEW.estado IN ('seleccionado', 'confirmado') AND OLD.estado <> NEW.estado AND (NEW.participante_id IS NULL OR NEW.participante_id = 0) BEGIN
INSERT OR IGNORE INTO evento_edicion_participante (evento_edicion_id, artista_id, estado) VALUES (NEW.evento_edicion_id, NEW.artista_id, 'activo');
UPDATE participante_exposicion SET participante_id = (SELECT id FROM evento_edicion_participante WHERE artista_id = NEW.artista_id AND evento_edicion_id = NEW.evento_edicion_id LIMIT 1) WHERE id = NEW.id;
END;
