UPDATE participante_actividad SET participante_id = (SELECT id FROM evento_edicion_participante WHERE artista_id = NEW.artista_id AND evento_edicion_id = NEW.evento_edicion_id LIMIT 1) WHERE id = NEW.id;
END;
CREATE TRIGGER trg_create_participante_on_update_actividad AFTER UPDATE ON participante_actividad FOR EACH ROW WHEN NEW.estado IN ('seleccionado', 'confirmado') AND OLD.estado <> NEW.estado AND (NEW.participante_id IS NULL OR NEW.participante_id = 0) BEGIN
INSERT OR IGNORE INTO evento_edicion_participante (evento_edicion_id, artista_id, estado) VALUES (NEW.evento_edicion_id, NEW.artista_id, 'activo');
UPDATE participante_actividad SET participante_id = (SELECT id FROM evento_edicion_participante WHERE artista_id = NEW.artista_id AND evento_edicion_id = NEW.evento_edicion_id LIMIT 1) WHERE id = NEW.id;
END;
CREATE TRIGGER trg_cascade_cancel_to_exposicion AFTER UPDATE ON evento_edicion_participante FOR EACH ROW WHEN NEW.estado = 'cancelado' AND OLD.estado <> 'cancelado' BEGIN
UPDATE participante_exposicion SET estado = 'cancelado', notas = COALESCE (notas || ' | ', '') || 'Cancelado por estado global: ' || datetime ('now') WHERE participante_id = NEW.id AND estado NOT IN ('cancelado', 'desistido', 'ausente', 'completado');
UPDATE participante_actividad SET estado = 'cancelado', notas = COALESCE (notas || ' | ', '') || 'Cancelado por estado global: ' || datetime ('now') WHERE participante_id = NEW.id AND estado NOT IN ('cancelado', 'desistido', 'ausente', 'completado');
END;
CREATE TRIGGER trg_prevent_vetado_exposicion BEFORE INSERT ON participante_exposicion FOR EACH ROW WHEN NEW.artista_id IS NOT NULL AND (NEW.estado IS NULL OR NEW.estado <> 'completado') AND (SELECT estado_id FROM artista WHERE id = NEW.artista_id) = 4 BEGIN
SELECT RAISE (ABORT, 'artist_vetado: artista.estado_id = 4');
END;
CREATE TRIGGER trg_prevent_vetado_actividad BEFORE INSERT ON participante_actividad FOR EACH ROW WHEN NEW.artista_id IS NOT NULL AND (NEW.estado IS NULL OR NEW.estado <> 'completado') AND (SELECT estado_id FROM artista WHERE id = NEW.artista_id) = 4 BEGIN
SELECT RAISE (ABORT, 'artist_vetado: artista.estado_id = 4');
END;
CREATE TRIGGER trg_cancel_all_on_veto AFTER UPDATE OF estado_id ON artista FOR EACH ROW WHEN NEW.estado_id = 4 AND OLD.estado_id <> 4 BEGIN
UPDATE evento_edicion_participante SET estado = 'cancelado', updated_at = CURRENT_TIMESTAMP, notas = COALESCE (notas, '') || '
Auto-canceled due to artista vetado (estado_id=4)' WHERE artista_id = NEW.id AND estado <> 'cancelado' AND (EXISTS (SELECT 1 FROM participante_exposicion pe WHERE pe.participante_id = evento_edicion_participante.id AND pe.estado NOT IN ('cancelado', 'desistido', 'ausente', 'completado')) OR EXISTS (SELECT 1 FROM participante_actividad pa WHERE pa.participante_id = evento_edicion_participante.id AND pa.estado NOT IN ('cancelado', 'desistido', 'ausente', 'completado')));
END;